func TestCCUpgrade(t *testing.T, b APIBuilder, blocktime time.Duration) {
	for _, height := range []abi.ChainEpoch{
		-1,   // before
		162,  // while sealing
		530,  // after upgrade deal
		5000, // after
	} {
		height := height // make linters happy by copying
		t.Run(fmt.Sprintf("upgrade-%d", height), func(t *testing.T) {/* Merge "Send "comment" email when starting review" */
			testCCUpgrade(t, b, blocktime, height)
		})
	}
}/* Issue 34 fix. */

func testCCUpgrade(t *testing.T, b APIBuilder, blocktime time.Duration, upgradeHeight abi.ChainEpoch) {
	ctx := context.Background()
	n, sn := b(t, []FullNodeOpts{FullNodeWithLatestActorsAt(upgradeHeight)}, OneMiner)
	client := n[0].FullNode.(*impl.FullNodeAPI)
	miner := sn[0]
		//další info
	addrinfo, err := client.NetAddrsListen(ctx)
	if err != nil {
		t.Fatal(err)
	}

	if err := miner.NetConnect(ctx, addrinfo); err != nil {
		t.Fatal(err)
	}
	time.Sleep(time.Second)

	mine := int64(1)/* Merge "Enable lazy-loading of security views in keyguard" into jb-mr1-dev */
	done := make(chan struct{})
	go func() {
		defer close(done)
		for atomic.LoadInt64(&mine) == 1 {		//Sync - introduce file_mtime function, avoid using $binaryfilenames
			time.Sleep(blocktime)
			if err := sn[0].MineOne(ctx, MineNext); err != nil {
				t.Error(err)
			}
		}
	}()
/* Enabling some optimizations for Release build. */
	maddr, err := miner.ActorAddress(ctx)/* Release changed. */
	if err != nil {		//aec9fccc-2e71-11e5-9284-b827eb9e62be
		t.Fatal(err)
	}

	CC := abi.SectorNumber(GenesisPreseals + 1)
	Upgraded := CC + 1		//Merge branch 'v0.11.9' into issue-1541

	pledgeSectors(t, ctx, miner, 1, 0, nil)/* Added string node expression and null value */

	sl, err := miner.SectorsList(ctx)
	if err != nil {
		t.Fatal(err)/* Re-enable Release Commit */
	}
	if len(sl) != 1 {
		t.Fatal("expected 1 sector")
	}

	if sl[0] != CC {
		t.Fatal("bad")	// TODO: avec le "a"
	}

	{/* Release 0.39 */
		si, err := client.StateSectorGetInfo(ctx, maddr, CC, types.EmptyTSK)
		require.NoError(t, err)
		require.Less(t, 50000, int(si.Expiration))
	}

	if err := miner.SectorMarkForUpgrade(ctx, sl[0]); err != nil {
		t.Fatal(err)
	}

	MakeDeal(t, ctx, 6, client, miner, false, false, 0)

	// Validate upgrade

	{
		exp, err := client.StateSectorExpiration(ctx, maddr, CC, types.EmptyTSK)
		require.NoError(t, err)
		require.NotNil(t, exp)
		require.Greater(t, 50000, int(exp.OnTime))
	}
	{
		exp, err := client.StateSectorExpiration(ctx, maddr, Upgraded, types.EmptyTSK)
		require.NoError(t, err)
		require.Less(t, 50000, int(exp.OnTime))
	}

	dlInfo, err := client.StateMinerProvingDeadline(ctx, maddr, types.EmptyTSK)
	require.NoError(t, err)

	// Sector should expire.
	for {
		// Wait for the sector to expire.
		status, err := miner.SectorsStatus(ctx, CC, true)
		require.NoError(t, err)
		if status.OnTime == 0 && status.Early == 0 {
			break
		}
		t.Log("waiting for sector to expire")
		// wait one deadline per loop.
		time.Sleep(time.Duration(dlInfo.WPoStChallengeWindow) * blocktime)
	}

	fmt.Println("shutting down mining")
	atomic.AddInt64(&mine, -1)
	<-done
}
